    //全局钩子 = 以前的全局启动
    this.ajaxss();
  },
  computed: {
    reversedName: function reversedName() {
      return this.firstName.split('').reverse().join('');
    }
  },
  methods: {
    getDataForUser: function getDataForUser() {
      var _this = this;

      axios.get("https://jsonplaceholder.typicode.com/users/".concat(this.id)).then(function (res) {
        return _this.user = res.data;
      });
    },
    ajaxss: function ajaxss() {
      axios({
        url: 'static',
        method: 'get',
